import React,{ useState } from 'react';
import CategoryButton from './CategoryButton';

let cities=["select a City","harrow","heathrow","stratford"];
function DropdownMenu() {
  const [city, setCity]=useState('');

  function handleCityNameChange(e) {
     e.target.value === "Filter by Region"
      ? setCity("")
      : setCity(e.target.value);
    console.log(e.target.value)
    console.log(city);
  };
  return (
    <div className='dropdownMenu'>
      <label for="city-select">Choose a City:</label>
      <select name="cities" id="city" onChange={(e) => handleCityNameChange(e)}>
        {/* <option value="city">Select a City</option> */}
        {cities.map((city,index)=>(
            <option key={index} value={city}>{city}</option>
        ))} 
      </select>
      <CategoryButton cityName={city}/>
    </div>
  )
}

export default DropdownMenu;
